member.example:
@model BSPL.Common.Models.LoginModel


<div class="modal-content">
<div class="input-container" id="userInputDialog" title="Login Dialog">
<form id="reportForm" method="post">
<h3>@ViewBag.L["Login"]</h3>
@if (!string.IsNullOrWhiteSpace(Model.Error))
{
<h4 style="color: red">@Model.Error</h4>
}
<fieldset>
<input placeholder='@ViewBag.L["Email ID"]' name="UserID" value="@Model.UserID" type="text" tabindex="1" required autofocus>
</fieldset>
<fieldset>
<input placeholder='@ViewBag.L["Password"]' name="Password" type="password" tabindex="2" required>
</fieldset>

@if (Model.SupportedLanguages.Count > 1)
{
<fieldset>
<select name="Locale">
@foreach (var l in Model.SupportedLanguages)
{
if (l.Name.Equals(Model.Locale))
{
<option value="@l.Name" selected="selected">@l.DisplayName</option>
}
else
{
<option value="@l.Name">@l.DisplayName</option>
}
}
</select>
</fieldset>
}
else
{
<input type="hidden" name="Locale" value="@Model.Locale" />
}

<fieldset>
<button name="Login" type="submit">@ViewBag.L["Login"]</button>
</fieldset>
</form>
</div>
</div>

